<script type="text/javascript">
	// <![CDATA[
	var SGSkin = WP.cookie.get('SGSkin');
	if (typeof(SGSkin)=='object') { SGSkin=0; }

	var SGP = WP.cookie.get('SGProfil');
	if (SGP<=1) {
		$(document.body).addClass('SGPklasyczna');
		if (SGSkin==0) SGSkin=2;
	} else if (SGP==2) {
		$(document.body).addClass('SGPnapowaznie');
		if (SGSkin==0) SGSkin=4;
	} else if (SGP==3) {
		$(document.body).addClass('SGPnasportowo');
		if (SGSkin==0) SGSkin=1; 
	} else if (SGP==4){
		$(document.body).addClass('SGPnaluzie');
		if (SGSkin==0) SGSkin=9;
	}

	//skin
	$(document.body).addClass('SGSkin'+SGSkin);
	//skin 9 - zielony mix
	if (SGSkin==9){
		var x = document.createElement('div');
		x.id='SGSbg';
		x.style.position = 'absolute';
		x.style.width='100%';
		x.style.height='460px';
		x.style.zIndex=-1;
		x.style.background='url(i/ivar/layout/200812/skin_9_bg.jpg) no-repeat top center';
		//document.body.insertBefore(x, document.getElementById('wpHeader'));
		document.body.insertBefore(x, document.getElementById('grPrezentacjaBar'));
	}

	ENTRY_HIT = '&SGP='+SGP+'&SGS='+SGSkin+'&SGGF='+WP.mcookie.get('gloFlash');
	
/* dopasowanie dlugosci bx (ilosc linkow w boksie w zaleznosci od profilu)*/
	var bxDLTab = {
		1:{
			'bxWiadomosci':10,
			'bxSport':10,
			'bxTech':6,
			'bxMoto':6
		},
		2:{
			'bxPrasa':7,
			'bxKultura':6,
			'bxCiekawostki':6,
			'bxSport':9
		},
		3:{
			'bxRozrywka':8,
			'bxWiadomosci':8
		},
		4:{
			'bxWiadomosci':8,
			'bxSport':8,
			'bxTech':5
		}
	};
	//ukrywanie nadmiarowych ilosci linkow (w polaczeniu z powyzsza lista)
	var SGPP = SGP<1?1:SGP;
	for (var id in bxDLTab[SGPP]) if(ID(id+'Lista')){
		var dt = ID(id+'Lista').getElementsByTagName('li');
		var dl = dt.length;
		for (var i=dl; i>=bxDLTab[SGPP][id]; i--) {
			$(dt[i]).hide();
		}
	}

var rotateTime = 8 * 1000;
var animT = [100, 50, 20, 8, 3, 1, 0];
var dnActive=0, dnT = ['Poleca', 'Produkty'], dnTRev=[], dnTH=[];
for (var i=0;i<dnT.length; i++) dnTRev[dnT[i]] = i; //rev table
var dnLock = false, dnHLock = -1, dnRTS = -1;
	
WP.event.set('ondomload', function(){
	dnActivate(0);
}); 
WP.event.set('onload', function(){
	setTimeout('dnRotator()', 3000);
});

//rotowanie bąbelków
function dnRotator(n){
	if (dnLock==false) dnActivate(++dnActive);
	setTimeout('dnRotator()', rotateTime); //kolejne rotate
}

//akcje na nagłówkach
for (var i=0;i<dnT.length; i++) {
	ID('ng'+dnT[i]).onmousedown = function(e){ dnActivate(dnTRev[WP.event.getNode(e).id.substr(2)]) };
	ID('ng'+dnT[i]).getElementsByTagName('h2')[0].onmousedown = function(e){ dnActivate(dnTRev[WP.event.getNode(e).parentNode.id.substr(2)]) };
}


/* pokazuje odpowiednia zakładkę */
function dnActivate(n){
	if (typeof(n)=='undefined') 
		n = dnActive;
	else {
		if (n>=dnT.length) n=0;
		if (n>=0) dnActive = n;
	}
	//hide/show
	for (var i=0;i<dnT.length; i++) {
		ID('dn'+dnT[i]).style.overflow = 'hidden';
		if (i==n){
			ID('ng'+dnT[i]+'Act').style.display='block';
			dnShow(i);
		} else {
			ID('ng'+dnT[i]+'Act').style.display='none';
			dnHide(i);
		}
	}
}

$('bxBabelki').onmouseover = function(e){ dnLock=true }
$('bxBabelki').onmouseout  = function(e){ dnLock=false }

function dnShow(n,h){
	if (typeof(h)=='undefined'){
		var height = ID('dn'+dnT[n]).clientHeight||ID('dn'+dnT[n]).offsetHeight;
		if ( height<2 ) { dnLock = true; setTimeout('dnShow('+n+', 0)', 25) }
	} else {
	//liczenie h (zmiana wysokosci babelkow (zwijanie, rozwijanie)
	for (var i=0;i<dnT.length; i++) {
		dnTH[i] = ID('dn'+dnT[i]+'Cnt').clientHeight||ID('dn'+dnT[i]+'Cnt').offsetHeight;
	}
		if (h<animT.length-1) {
			h++;
			ID('dn'+dnT[n]).style.height =  dnTH[n]-animT[h] + 'px';
			setTimeout('dnShow('+n+', '+h+')', 50);
		} else {
			ID('dn'+dnT[n]).style.height = dnTH[n] + 'px';
			dnLock = false;
		} 
		if (WP.isIE) ID('dn'+dnT[n]+'Cnt').style.zoom = 1;
	}
}
function dnHide(n,h){
	if (typeof(h)=='undefined'){
		var height = ID('dn'+dnT[n]).clientHeight||ID('dn'+dnT[n]).offsetHeight;
		if ( height>1 ) setTimeout('dnHide('+n+',0)', 25);
	} else {  
		if (animT[h]) ID('dn'+dnT[n]).style.height = animT[h] + 'px';
		if (h<animT.length-1) setTimeout('dnHide('+n+','+(++h)+')', 50);
	} 
}

	// ]]>
</script>

